const initialState = {
    message: 'Learning Redux in React'
}

const reducer = (state = initialState, action) => {
    const newState = {...state}

    if (action.type === 'Message Change'){
        newState = 'Looks like it Worked'
    }
    return newState
}

export default reducer;
